DROP TABLE IF EXISTS `product_info`;
CREATE TABLE `product_info` (
  `ITEM_ID` varchar(36) NOT NULL COMMENT '商品ID',
  `HIRER_ID` varchar(36) NOT NULL COMMENT '租户ID',
  `CATEGORY_ID` varchar(36) NOT NULL COMMENT '产品目录ID',
  `ITEM_CODE` varchar(45) NOT NULL COMMENT '商品编码',
  `ITEM_NAME` varchar(100) NOT NULL COMMENT '商品名称',
  `BRAND` varchar(45) DEFAULT NULL COMMENT '品牌',
  `KEY_WORD` varchar(200) DEFAULT NULL COMMENT '搜索关键字',
  `HAVE_SN` varchar(1) CHARACTER SET big5 DEFAULT NULL COMMENT '是否有串号，见字典【IS_HAVE】{Y:有，N:无}',
  `ONLINE_STATUS` varchar(1) DEFAULT NULL COMMENT '上架状态：见字典【ONLINE_STATUS】{1：在库，2：待审批，3：上架，4：下架}',
  `ONLINE_DATE` timestamp NULL DEFAULT NULL COMMENT '上架时间',
  `SALE_POINT` varchar(400) NOT NULL COMMENT '卖点',
  `SHORT_CODE` varchar(45) DEFAULT NULL COMMENT '助记码',
  `PRODUCT_TYPE` varchar(2) DEFAULT NULL COMMENT '产品类型：见字典【PRODUCT_TYPE】{1:单品，2：组合产品}',
  `STOCK_REDUCE_TYPE` varchar(1) DEFAULT NULL COMMENT '库存扣减方式:见字典【STOCK_REDUCE_TYPE】{1：订单提交扣减，2：订单支付扣减}',
  `INSTOCK_TYPE` varchar(1) DEFAULT NULL COMMENT '入库方式：见字典【INSTOCK_TYPE】{1：采购接收时入库，2：销售时入库}\n',
  `ENABLE_FLAG` varchar(1) DEFAULT NULL COMMENT '是否有效',
  `COMMENTS` varchar(500) DEFAULT NULL COMMENT '备注',
  `AFTER_SALES_SERVICE` varchar(500) DEFAULT NULL COMMENT '售后服务',
  `CREATE_BY` int(11) DEFAULT NULL COMMENT '创建人',
  `CREATE_DATE` timestamp NULL DEFAULT NULL COMMENT '创建时间',
  `LAST_UPDATE_BY` int(11) DEFAULT NULL COMMENT '修改人',
  `LAST_UPDATE_DATE` timestamp NULL DEFAULT NULL COMMENT '修改时间',
  PRIMARY KEY (`ITEM_ID`),
  UNIQUE KEY `PRODUCT_INFO_UK` (`ITEM_CODE`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='商品信息';

